import React, { useEffect, useMemo, useRef, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import { doc, getDoc } from "firebase/firestore";
import { db } from "../../firebase";
import {
  calculateScoresFromState,
  calculateCompletionFromState,
  countNonZeroWeightageMetrics,
  getPerformanceCategory,
  getCriticalMetrics,
  generateInsights,
} from "../../scoring";
import "./ScoreDashboard.css";

const ScoreDashboard = ({ user }) => {
  const navigate = useNavigate();
  const { collectionName, assessmentId } = useParams();
  const [appState, setAppState] = useState(null);
  const [meta, setMeta] = useState(null);
  const [loading, setLoading] = useState(true);
  const chartRef = useRef(null);
  const chartInstanceRef = useRef(null);

  useEffect(() => {
    const fetchAssessment = async () => {
      try {
        if (!user || !user.uid) return;
        const assessmentRef = doc(
          db,
          "users",
          user.uid,
          collectionName,
          assessmentId
        );
        const snap = await getDoc(assessmentRef);
        if (!snap.exists()) {
          setLoading(false);
          return;
        }
        const data = snap.data();
        setMeta({
          respondentName: data.respondentName,
          organisation: data.organisation,
          city: data.city,
          borough: data.borough,
          ward: data.ward,
          createdAt: data.createdAt,
        });
        setAppState(
          data.ratings && data.priorities && data.weights
            ? {
                ratings: data.ratings,
                priorities: data.priorities,
                weights: data.weights,
              }
            : null
        );
      } catch (err) {
        console.error("Error loading assessment for score dashboard:", err);
      } finally {
        setLoading(false);
      }
    };

    fetchAssessment();
  }, [user, collectionName, assessmentId]);

  const computed = useMemo(() => {
    if (!appState) {
      return null;
    }
    const scores = calculateScoresFromState(appState);
    const completionPercent = calculateCompletionFromState(appState);
    const nonZeroMetrics = countNonZeroWeightageMetrics(appState);
    const performanceCategory = getPerformanceCategory(scores.overall);
    const criticalMetrics = getCriticalMetrics(appState);
    const insights = generateInsights(scores);
    return {
      scores,
      completionPercent,
      nonZeroMetrics,
      performanceCategory,
      criticalMetrics,
      insights,
    };
  }, [appState]);

  useEffect(() => {
    if (!computed || !chartRef.current) return;
    const { scores } = computed;
    const canvas = chartRef.current;
    const ctx = canvas.getContext("2d");
    const { Chart } = window;
    if (!Chart) {
      console.error("Chart.js not loaded");
      return;
    }

    // Destroy previous chart instance if exists
    if (chartInstanceRef.current) {
      chartInstanceRef.current.destroy();
    }

    const labels = Object.keys(scores.dimensions);
    const data = Object.values(scores.dimensions).map((d) => d.score);

    // Create gradient for bars
    const createGradient = () => {
      const gradient = ctx.createLinearGradient(0, 0, 0, 400);
      gradient.addColorStop(0, 'rgba(139, 92, 246, 0.8)');
      gradient.addColorStop(1, 'rgba(139, 92, 246, 0.4)');
      return gradient;
    };

    // Create hover gradient
    const createHoverGradient = () => {
      const gradient = ctx.createLinearGradient(0, 0, 0, 400);
      gradient.addColorStop(0, 'rgba(139, 92, 246, 1)');
      gradient.addColorStop(1, 'rgba(139, 92, 246, 0.6)');
      return gradient;
    };

    chartInstanceRef.current = new Chart(ctx, {
      type: "bar",
      data: {
        labels: labels.map((l) =>
          l.length > 20 ? `${l.substring(0, 20)}...` : l
        ),
        datasets: [
          {
            label: "Dimension Score",
            data,
            backgroundColor: createGradient(),
            borderColor: '#7c3aed',
            borderWidth: 1,
            borderRadius: 8,
            hoverBackgroundColor: createHoverGradient(),
            hoverBorderColor: '#6d28d9',
            hoverBorderWidth: 2,
          },
        ],
      },
      options: {
        responsive: true,
        maintainAspectRatio: false,
        animation: {
          duration: 1000,
          easing: 'easeOutQuart'
        },
        scales: {
          y: {
            beginAtZero: true,
            max: 5,
            grid: {
              color: 'rgba(226, 232, 240, 0.3)',
              drawBorder: false,
            },
            ticks: {
              color: "#64748b",
              font: {
                size: 12,
                family: "'Inter', sans-serif"
              },
              callback: (value) => `${value}/5`,
              padding: 8,
            },
            border: {
              display: false,
            }
          },
          x: {
            grid: {
              display: false,
            },
            ticks: {
              color: "#475569",
              font: {
                size: 12,
                family: "'Inter', sans-serif",
                weight: 500
              },
              maxRotation: 45,
              minRotation: 45,
            },
            border: {
              display: false,
            }
          },
        },
        plugins: {
          legend: {
            display: false,
          },
          tooltip: {
            backgroundColor: 'rgba(15, 23, 42, 0.95)',
            titleColor: '#f8fafc',
            bodyColor: '#f8fafc',
            borderColor: '#475569',
            borderWidth: 1,
            cornerRadius: 8,
            padding: 12,
            displayColors: false,
            callbacks: {
              label: function(context) {
                const label = labels[context.dataIndex] || '';
                const fullLabel = label.length > 30 ? `${label.substring(0, 30)}...` : label;
                return `${fullLabel}: ${context.parsed.y.toFixed(2)}/5`;
              },
              title: function() {
                return '';
              }
            }
          },
        },
        interaction: {
          intersect: false,
          mode: 'index',
        },
      },
    });

    // Cleanup function
    return () => {
      if (chartInstanceRef.current) {
        chartInstanceRef.current.destroy();
      }
    };
  }, [computed]);

  const formatDate = (date) => {
    if (!date) return "N/A";
    try {
      const d = date.toDate ? date.toDate() : new Date(date);
      if (Number.isNaN(d.getTime())) return "N/A";
      return d.toLocaleDateString("en-US", {
        month: "short",
        day: "numeric",
        year: "numeric",
      });
    } catch (e) {
      return "N/A";
    }
  };

  // Add custom header CSS styles
  const headerStyles = {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'space-between',
    padding: '24px 32px',
    background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
    color: 'white',
    boxShadow: '0 4px 6px -1px rgba(0, 0, 0, 0.1)',
    marginBottom: '24px',
  };

  if (loading) {
    return (
      <div className="score-dashboard-loading">
        <div className="loading-spinner"></div>
        <p>Loading assessment results...</p>
      </div>
    );
  }

  if (!appState || !computed) {
    return (
      <div className="score-dashboard-loading">
        <p>No assessment results found.</p>
        <button className="btn-back-home" onClick={() => navigate("/")}>
          Return to Home
        </button>
      </div>
    );
  }

  const {
    scores,
    completionPercent,
    nonZeroMetrics,
    performanceCategory,
    criticalMetrics,
    insights,
  } = computed;

