import React, { useEffect, useMemo, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { signOut } from 'firebase/auth';
import { doc, getDoc } from 'firebase/firestore';
import Layout from '../Layout';
import { auth, db } from '../../firebase';
import './Home.css';

const Home = ({ user }) => {
  const navigate = useNavigate();
  const [username, setUsername] = useState('');

  useEffect(() => {
    let active = true;
    const loadProfile = async () => {
      if (!user?.uid) return;
      try {
        const snapshot = await getDoc(doc(db, 'users', user.uid));
        if (snapshot.exists() && active) {
          const data = snapshot.data();
          setUsername(data.username || '');
        }
      } catch (err) {
        // Silently ignore profile fetch errors; fallback to email.
      }
    };
    loadProfile();
    return () => { active = false; };
  }, [user]);

  const displayName = useMemo(() => username || user?.email || 'user', [username, user]);

  const handleLogout = async () => {
    await signOut(auth);
    navigate('/login');
  };

  return (
    <Layout>
      <div className="home-hero">
        <div className="home-badge">Dashboard</div>
        <h1>Hello, {displayName}</h1>
        <p>Welcome back! You&apos;re signed in and ready to explore.</p>
        <div className="home-actions">
          <button className="primary" onClick={handleLogout}>Log out</button>
        </div>
      </div>
    </Layout>
  );
};

export default Home;
